package com.coworkers.clinicpet.controller;import com.coworkers.clinicpet.model.dto.OwnerDTO;import com.coworkers.clinicpet.service.OwnerService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/owners")public class OwnerController {    @Autowired    private OwnerService ownerService;    @GetMapping    public ResponseEntity<List<OwnerDTO>> getAllOwners() {        List<OwnerDTO> owners = ownerService.getAllOwners();        return ResponseEntity.ok(owners);    }    @GetMapping("/{id}")    public ResponseEntity<OwnerDTO> getOwnerById(@PathVariable Long ownerId) {        OwnerDTO owner = ownerService.getOwnerById(ownerId);        return new ResponseEntity<>(owner, HttpStatus.OK);    }    @PostMapping    public ResponseEntity<OwnerDTO> createOwner(@RequestBody OwnerDTO ownerDTO) {        OwnerDTO owner = ownerService.createOwner(ownerDTO);        return ResponseEntity.ok(ownerDTO);    }    @PutMapping("/{id}")    public ResponseEntity<OwnerDTO> updateOwner(@PathVariable Long ownerId, @RequestBody OwnerDTO ownerDTO) {        OwnerDTO updatedOwner = ownerService.updateOwner(ownerDTO, ownerId);        return ResponseEntity.ok(updatedOwner);    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deleteOwner(@PathVariable Long id) {        ownerService.deleteOwner(id);        return ResponseEntity.noContent().build();    }}