package com.coworkers.clinicpet.controller;import com.coworkers.clinicpet.model.dto.PetDTO;import com.coworkers.clinicpet.service.PetService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/pets")public class PetController {    @Autowired    private PetService petService;    @PostMapping    public ResponseEntity<PetDTO> createPet(@RequestBody PetDTO petDTO) {        PetDTO newPet = petService.createPet(petDTO);        return ResponseEntity.ok(newPet);    }    @GetMapping()    public ResponseEntity<List<PetDTO>> getAllPets() {        List<PetDTO> pets = petService.getAllPets();        return ResponseEntity.ok(pets);    }    @GetMapping("/{id}")    public ResponseEntity<PetDTO> getPetById(@PathVariable Long id) {        PetDTO pet = petService.getPetById(id);        return new ResponseEntity<>(pet, HttpStatus.OK);    }    @PutMapping("/{id}")    public ResponseEntity<PetDTO> updatePet(@PathVariable Long id, @RequestBody PetDTO petDTO) {        PetDTO updatedPet = petService.updatePet(petDTO, id);        return ResponseEntity.ok(updatedPet);    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deletePet(@PathVariable Long id) {        petService.deletePet(id);        return ResponseEntity.noContent().build();    }}