package com.coworkers.clinicpet.controller;import com.coworkers.clinicpet.model.entities.Pet;import com.coworkers.clinicpet.service.PetService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/pets")public class PetController {    private final PetService petService;    public PetController(PetService petService) {        this.petService = petService;    }    @GetMapping("/{petId}")    public ResponseEntity<Pet> getPetById(@PathVariable Long petId) {        Pet pet = petService.getUserById(petId);        return pet != null ? ResponseEntity.ok(pet) : ResponseEntity.notFound().build();    }    @PostMapping()    public ResponseEntity<Void> savePet(@RequestBody Pet pet) {        try {            petService.savePet(pet);            return ResponseEntity.status(201).build();        }catch (Exception e){         return ResponseEntity.status(400).build();        }    }}