package com.coworkers.clinicpet.service;import com.coworkers.clinicpet.model.Pet;import java.util.ArrayList;import java.util.List;public class PetService {    private final List<Pet> pets = new ArrayList<>();    public PetService() {        Pet dog = new Pet("1", "dog", 1);        Pet cat = new Pet("2", "cat", 9);        pets.add(dog);        pets.add(cat);    }    public Pet getUserById(String id) {        for (Pet current : pets) {            if (current.getId().equals(id)) {                return current;            }        }        throw new RuntimeException("Pet not found");    }    public void savePet(Pet pet) {        String dog = "dog";        if (pet.getAnimalType().equals(dog)) {            throw new RuntimeException();        }        this.pets.add(pet);    }}